import React, {Component} from 'react';
import { Link } from 'react-router-dom';
import firebase from '../../Firebase';
import { auth, firestore } from '../../Firebase';


class Cadastro extends Component {
  state = {
    email: '',
    password: '',
    firstName: '',
    lastName: '',
    birthDate: '',
    errorMessage: '',
  };

  handleInputChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };

  handleSignUp = async () => {
    const { email, password, firstName, lastName, birthDate } = this.state;

    try {
      const userCredential = await auth.createUserWithEmailAndPassword(email, password);

      await firestore.collection('users').doc(userCredential.user.uid).set({
        firstName,
        lastName,
        birthDate,
      });

      this.props.history.push('/login');
    } catch (error) {
      this.setState({ errorMessage: error.message });
    }
  };

  render() {
    return (
      <div>
        <h2>Sign Up</h2>
        <input type="text" name="email" placeholder="Email" onChange={this.handleInputChange} />
        <input type="password" name="password" placeholder="Password" onChange={this.handleInputChange} />
        <input type="text" name="firstName" placeholder="First Name" onChange={this.handleInputChange} />
        <input type="text" name="lastName" placeholder="Last Name" onChange={this.handleInputChange} />
        <input type="date" name="birthDate" onChange={this.handleInputChange} />
        <button onClick={this.handleSignUp}>Sign Up</button>
        {this.state.errorMessage && <p>{this.state.errorMessage}</p>}
      </div>
    );
  }
}

export default Cadastro;
